import './App.css';
import Axios from "axios";
import React, { useEffect, useState } from 'react';
import { Button, Container, Navbar, Spinner, Alert } from 'react-bootstrap';


function App() {

  const [catFact, setCatFact] = useState('');
  const [dogFact, setDogFact] = useState('');

  const fetchCatFact = () =>{
    Axios.get('https://catfact.ninja/fact')
      .then((res) => {
        setCatFact(res.data.fact);
      });
  }

  const fetchDogFact = () => {
    Axios.get('https://dog-api.kinduff.com/api/facts')
      .then((res) => {
        const randomIndex = Math.floor(Math.random() * res.data.facts.length);
        const randomFact = res.data.facts[randomIndex];
        setDogFact(randomFact);
      })
  }


  useEffect(() =>{
    Axios.get('https://catfact.ninja/fact')
      .then((res) => {
        setCatFact(res.data.fact);
      });
  },[]);

  useEffect(() =>{
    Axios.get('https://dog-api.kinduff.com/api/facts')
      .then((res) => {
        setDogFact(res.data.fact);
      });
  },[]);


  return (
    <Container style={{ backgroundColor: '#f8f9fa', minHeight: '100vh', padding: '20px' }}>
      <Navbar bg="dark" variant="dark">
        <Navbar.Brand>Pet Facts Generator</Navbar.Brand>
      </Navbar>

      <Container className="text-center mt-5">
        {error && <Alert variant="danger">{error}</Alert>}
        {loading ? (
          <Spinner animation="border" />
        ) : (
          <>
            <Button onClick={fetchCatFact} className="btn-lg m-2" variant="primary">
              Generate Cat Fact
            </Button>
            <p>{catFact}</p>

            <Button onClick={fetchDogFact} className="btn-lg m-2" variant="primary">
              Generate Dog Fact
            </Button>
            <p>{dogFact}</p>
          </>
        )}
      </Container>
    </Container>
  );
}

export default App;
