import './App.css';
import Axios from "axios";
import React, { useEffect, useState } from 'react';


function App() {

  const [catFact, setCatFact] = useState('');
  const [dogFact, setDogFact] = useState('');

  const fetchCatFact = () =>{
    Axios.get('https://catfact.ninja/fact')
      .then((res) => {
        setCatFact(res.data.fact);
      });
  }

  const fetchDogFact = () => {
    Axios.get('https://dog-api.kinduff.com/api/facts')
      .then((res) => {
        const randomIndex = Math.floor(Math.random() * res.data.facts.length);
        const randomFact = res.data.facts[randomIndex];
        setDogFact(randomFact);
      })
  }


  useEffect(() =>{
    Axios.get('https://catfact.ninja/fact')
      .then((res) => {
        setCatFact(res.data.fact);
      });
  },[]);

  useEffect(() =>{
    Axios.get('https://dog-api.kinduff.com/api/facts')
      .then((res) => {
        setDogFact(res.data.fact);
      });
  },[]);


  return (
    <div style={{ backgroundColor: '#cceeff', minHeight: '100vh' }}>
      <div className="container mx-auto px-4">
        <header className="text-center">
          <h1 className="text-5xl font-bold text-green-600 mb-6">Pet Facts Generator</h1>
          <h2 className="text-lg mb-4">
            Do you have a pet? Well, it doesn't matter; learn about cats and dogs now by generating facts.
          </h2>

          <div className="my-6">
            <h3 className="text-2xl font-semibold mb-3">Do you want to learn about cats? Generate facts.</h3>
            <button onClick={fetchCatFact} className="text-white bg-green-500 hover:bg-green-600 px-6 py-2 rounded shadow-lg">
              Generate Cat Fact
            </button>
            <p className="text-green-800 mt-2">{catFact}</p>
          </div>

          <div className="my-6">
            <h3 className="text-2xl font-semibold mb-3">Do you want to learn about dogs? Generate facts.</h3>
            <button onClick={fetchDogFact} className="text-white bg-blue-500 hover:bg-blue-600 px-6 py-2 rounded shadow-lg">
              Generate Dog Fact
            </button>
            <p className="text-blue-800 mt-2">{dogFact}</p>
          </div>
        </header>
      </div>
    </div>
  );
}

export default App;
