import './App.css';
import Axios from "axios";
import React, { useEffect, useState } from 'react';


function App() {

  const [catFact, setCatFact] = useState('');
  const [dogFact, setDogFact] = useState('');

  const fetchCatFact = () =>{
    Axios.get('https://catfact.ninja/fact')
      .then((res) => {
        setCatFact(res.data.fact);
      });
  }

  const fetchDogFact = () => {
    Axios.get('https://dog-api.kinduff.com/api/facts')
      .then((res) => {
        const randomIndex = Math.floor(Math.random() * res.data.facts.length);
        const randomFact = res.data.facts[randomIndex];
        setDogFact(randomFact);
      })
  }


  useEffect(() =>{
    Axios.get('https://catfact.ninja/fact')
      .then((res) => {
        setCatFact(res.data.fact);
      });
  },[]);

  useEffect(() =>{
    Axios.get('https://dog-api.kinduff.com/api/facts')
      .then((res) => {
        setDogFact(res.data.fact);
      });
  },[]);


  return (
    <div>
      <nav className="navbar navbar-dark bg-dark">
        <div className="container">
          <span className="navbar-brand mb-0 h1">Pet Facts Generator</span>
        </div>
      </nav>

      <div className="container mt-4">
        <header>
          <h2 className="text-center mb-4">
            Do you have a pet? Well, it doesn't matter; learn about cats and dogs now by generating facts.
          </h2>

          <div className="my-6">
            <h3 className="text-center text-lg font-semibold mb-3">Do you want to learn about cats? Generate facts.</h3>
            <button onClick={fetchCatFact} className="btn btn-primary btn-lg">
              Generate Cat Fact
            </button>
            <p className="text-center mt-2">{catFact}</p>
          </div>

          <div className="my-6">
            <h3 className="text-center text-lg font-semibold mb-3">Do you want to learn about dogs? Generate facts.</h3>
            <button onClick={fetchDogFact} className="btn btn-primary btn-lg">
              Generate Dog Fact
            </button>
            <p className="text-center mt-2">{dogFact}</p>
          </div>
        </header>
      </div>
    </div>
  );
}

export default App;
