import './App.css';
import Axios from "axios";
import React, { useEffect, useState } from 'react';


function App() {

  const [catFact, setCatFact] = useState('');
  const [dogFact, setDogFact] = useState('');

  const fetchCatFact = () =>{
    Axios.get('https://catfact.ninja/fact')
      .then((res) => {
        setCatFact(res.data.fact);
      });
  }

  const fetchDogFact = () => {
    Axios.get('https://dog-api.kinduff.com/api/facts')
      .then((res) => {
        const randomIndex = Math.floor(Math.random() * res.data.facts.length);
        const randomFact = res.data.facts[randomIndex];
        setDogFact(randomFact);
      })
  }


  useEffect(() =>{
    Axios.get('https://catfact.ninja/fact')
      .then((res) => {
        setCatFact(res.data.fact);
      });
  },[]);

  useEffect(() =>{
    Axios.get('https://dog-api.kinduff.com/api/facts')
      .then((res) => {
        setDogFact(res.data.fact);
      });
  },[]);


  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-300 to-blue-500 flex flex-col justify-center items-center">
      <div className="container mx-auto px-4">
        <header className="text-center">
          <div className="bg-white rounded-lg p-6 mb-6">
            <h1 className="text-5xl font-bold mb-6 text-black">Pet Facts Generator</h1>
            <div className="my-6">
              <h3 className="text-2xl font-semibold mb-3 text-black">Do you want to learn about cats? Generate facts.</h3>
              <button onClick={fetchCatFact} className="btn btn-primary">
                Generate Cat Fact
              </button>
              <p className="mt-2">{catFact}</p>
            </div>

            <div className="my-6">
              <h3 className="text-2xl font-semibold mb-3 text-black">Do you want to learn about dogs? Generate facts.</h3>
              <button onClick={fetchDogFact} className="btn btn-primary">
                Generate Dog Fact
              </button>
              <p className="mt-2">{dogFact}</p>
            </div>
          </div>
        </header>
      </div>
    </div>
  );
}

export default App;
